import React, { useState, useEffect } from 'react';
import { Paper, Typography, Box, Checkbox, FormControlLabel, Button } from '@mui/material';
import axios from 'axios';

function DocumentManager({ onSelectionChange }) {
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get('http://localhost:8000/documents');
      setDocuments(response.data.documents);
    } catch (error) {
      setError('Failed to fetch documents');
    }
  };

  const handleSelectionChange = (document) => {
    const newSelection = selectedDocs.includes(document)
      ? selectedDocs.filter(doc => doc !== document)
      : [...selectedDocs, document];
    
    setSelectedDocs(newSelection);
    onSelectionChange(newSelection);
  };

  return (
    <Paper elevation={3} sx={{ p: 3, mb: 2 }}>
      <Typography variant="h6" gutterBottom>
        Available Documents
      </Typography>
      {error && (
        <Typography color="error" sx={{ mb: 2 }}>
          {error}
        </Typography>
      )}
      <Box>
        {documents.map((doc) => (
          <FormControlLabel
            key={doc}
            control={
              <Checkbox
                checked={selectedDocs.includes(doc)}
                onChange={() => handleSelectionChange(doc)}
              />
            }
            label={doc}
          />
        ))}
      </Box>
      {documents.length === 0 && (
        <Typography color="text.secondary">
          No documents uploaded yet
        </Typography>
      )}
    </Paper>
  );
}

export default DocumentManager;
