import React, { useState } from 'react';
import { ThemeProvider, createTheme, Container, CssBaseline } from '@mui/material';
import QueryInterface from './components/queryinterface';
import FileUpload from './components/fileupload';
import DocumentManager from './components/documentmanager';
import '@fontsource/montserrat';
import './styles/main.css';

const theme = createTheme({
  palette: {
    background: {
      default: '#fdf6e3', // Solarized Light background
    },
    primary: {
      main: '#cb4b16', // Orange
    },
  },
  typography: {
    fontFamily: 'Montserrat, sans-serif',
  },
});

function App() {
  const [selectedDocuments, setSelectedDocuments] = useState([]);

  const handleDocumentSelection = (documents) => {
    setSelectedDocuments(documents);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="md" sx={{ py: 4 }}>
        <h1>RAG System Interface</h1>
        <FileUpload onUploadSuccess={() => document.location.reload()} />
        <DocumentManager onSelectionChange={handleDocumentSelection} />
        <QueryInterface selectedDocuments={selectedDocuments} />
      </Container>
    </ThemeProvider>
  );
}

export default App;
