import React from 'react';
import { Paper, Typography, Box, Divider } from '@mui/material';
import './queryresults.css';

function LoadingBubbles() {
  return (
    <Box sx={{ 
      display: 'flex', 
      alignItems: 'flex-start',
      mb: 1
    }}>
      <Box className="typing-indicator">
        <span></span>
        <span></span>
        <span></span>
      </Box>
    </Box>
  );
}

function QueryResults({ messages, isLoading }) {
  if (!messages || messages.length === 0) return null;

  return (
    <Paper 
      elevation={0} 
      sx={{ 
        p: 3, 
        mb: 2, 
        mt: 2, 
        maxHeight: '60vh', 
        overflow: 'auto',
        backgroundColor: '#f8fafc',
        '&::-webkit-scrollbar': {
          width: '8px',
        },
        '&::-webkit-scrollbar-track': {
          background: '#f1f5f9',
          borderRadius: '4px',
        },
        '&::-webkit-scrollbar-thumb': {
          background: '#cbd5e1',
          borderRadius: '4px',
          '&:hover': {
            background: '#94a3b8',
          },
        },
      }}
    >
      {messages.map((message, index) => (
        <Box key={index} sx={{ mb: 3 }}>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'flex-start', 
            mb: 1,
            justifyContent: message.type === 'question' ? 'flex-end' : 'flex-start'
          }}>
            <Typography 
              variant="body1" 
              sx={{ 
                bgcolor: message.type === 'question' ? 'primary.main' : 'white',
                color: message.type === 'question' ? 'white' : 'text.primary',
                p: 2,
                borderRadius: 2,
                maxWidth: '80%',
                borderTopRightRadius: message.type === 'question' ? 4 : 16,
                borderTopLeftRadius: message.type === 'question' ? 16 : 4,
                boxShadow: message.type === 'question' 
                  ? 'none'
                  : '0 1px 2px rgba(0, 0, 0, 0.05)',
                fontSize: '0.95rem',
                lineHeight: 1.6,
              }}
            >
              {message.type === 'question' ? message.question : message.answer}
            </Typography>
          </Box>
          
          {message.type === 'answer' && message.sources && message.sources.length > 0 && (
            <Box sx={{ ml: 2, mt: 1, maxWidth: '80%' }}>
              <Typography 
                variant="caption" 
                color="text.secondary" 
                sx={{ 
                  display: 'block', 
                  mb: 1,
                  fontSize: '0.75rem',
                  fontWeight: 500
                }}
              >
                Sources:
              </Typography>
              {message.sources.map((source, sourceIndex) => (
                <Box key={sourceIndex} sx={{ mb: 1 }}>
                  <Typography 
                    variant="caption" 
                    color="text.secondary"
                    sx={{ fontSize: '0.75rem' }}
                  >
                    {source.source}
                  </Typography>
                </Box>
              ))}
            </Box>
          )}
          
          {index < messages.length - 1 && (
            <Divider sx={{ my: 3, opacity: 0.1 }} />
          )}
        </Box>
      ))}
      {isLoading && <LoadingBubbles />}
    </Paper>
  );
}

export default QueryResults;
