import React, { useState } from 'react';
import { ThemeProvider, createTheme, Container, CssBaseline, Typography, Box, Grid } from '@mui/material';
import QueryInterface from './components/queryinterface';
import FileUpload from './components/fileupload';
import DocumentManager from './components/documentmanager';
import '@fontsource/montserrat';
import './styles/main.css';

const theme = createTheme({
  palette: {
    background: {
      default: '#fdf6e3',
      paper: 'rgba(255, 255, 255, 0.9)',
    },
    primary: {
      main: '#cb4b16',
      light: '#dc322f',
    },
  },
  typography: {
    fontFamily: 'Montserrat, sans-serif',
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          transition: 'all 0.3s ease-in-out',
          '&:hover': {
            boxShadow: '0 8px 24px rgba(0,0,0,0.1)',
          },
          backdropFilter: 'blur(10px)',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: '8px',
          textTransform: 'none',
          transition: 'all 0.3s ease-in-out',
          '&:hover': {
            transform: 'translateY(-2px)',
          },
        },
      },
    },
  },
});

function App() {
  const [selectedDocuments, setSelectedDocuments] = useState([]);
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  const handleDocumentSelection = (documents) => {
    setSelectedDocuments(documents);
  };

  const handleUploadSuccess = () => {
    setRefreshTrigger(prev => prev + 1);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ 
        py: 4,
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}>
        <Box sx={{ 
          textAlign: 'center', 
          mb: 4,
          transform: 'scale(1)',
          transition: 'transform 0.3s ease-in-out',
          '&:hover': {
            transform: 'scale(1.02)',
          }
        }}>
          <Typography 
            variant="h2" 
            component="h1" 
            sx={{ 
              fontWeight: 'bold',
              background: 'linear-gradient(45deg, #cb4b16 30%, #dc322f 90%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              letterSpacing: '-1px',
            }}
          >
            vRAG
          </Typography>
        </Box>
        <Grid container spacing={3} sx={{ flex: 1 }}>
          <Grid item xs={3}>
            <FileUpload onUploadSuccess={handleUploadSuccess} />
          </Grid>
          <Grid item xs={6}>
            <QueryInterface selectedDocuments={selectedDocuments} />
          </Grid>
          <Grid item xs={3}>
            <DocumentManager 
              onSelectionChange={handleDocumentSelection} 
              refreshTrigger={refreshTrigger}
            />
          </Grid>
        </Grid>
      </Container>
    </ThemeProvider>
  );
}

export default App;
