import React, { useState, useEffect } from 'react';
import { Paper, Typography, Box, Checkbox, FormControlLabel, CircularProgress, IconButton, Tooltip } from '@mui/material';
import RefreshIcon from '@mui/icons-material/Refresh';
import axios from 'axios';

function DocumentManager({ onSelectionChange, refreshTrigger }) {
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [lastRefresh, setLastRefresh] = useState(new Date());

  useEffect(() => {
    fetchDocuments();
  }, [refreshTrigger, lastRefresh]);

  const handleRefresh = () => {
    setLastRefresh(new Date());
  };

  const fetchDocuments = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.get('http://localhost:8000/documents');
      
      if (response.data && Array.isArray(response.data.documents)) {
        setDocuments(response.data.documents);
        // Clear selected docs if they're no longer available
        const unavailableDocs = selectedDocs.filter(doc => !response.data.documents.includes(doc));
        if (unavailableDocs.length > 0) {
          const newSelection = selectedDocs.filter(doc => response.data.documents.includes(doc));
          setSelectedDocs(newSelection);
          onSelectionChange(newSelection);
          setError(`Some selected documents are no longer available: ${unavailableDocs.join(', ')}`);
        }
      } else {
        setError('Server returned invalid document list format. Please try refreshing.');
        setDocuments([]);
      }
    } catch (error) {
      console.error('Error fetching documents:', error);
      let errorMessage = 'Failed to fetch documents. ';
      if (error.response) {
        errorMessage += error.response.data?.message || `Server error: ${error.response.status}`;
      } else if (error.request) {
        errorMessage += 'No response from server. Please check your connection.';
      } else {
        errorMessage += error.message || 'Unknown error occurred.';
      }
      setError(errorMessage);
      setDocuments([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSelectionChange = (document) => {
    const newSelection = selectedDocs.includes(document)
      ? selectedDocs.filter(doc => doc !== document)
      : [...selectedDocs, document];
    
    setSelectedDocs(newSelection);
    onSelectionChange(newSelection);
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', mb: 2 }}>
        <Typography variant="h6" component="div">
          Available Documents
        </Typography>
        <Tooltip title="Refresh document list">
          <IconButton 
            onClick={handleRefresh} 
            disabled={loading}
            size="small"
            sx={{ ml: 1 }}
          >
            <RefreshIcon />
          </IconButton>
        </Tooltip>
      </Box>
      
      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
          <CircularProgress size={24} />
        </Box>
      )}
      
      {error && (
        <Typography 
          color="error" 
          sx={{ 
            mb: 2, 
            px: 2,
            py: 1, 
            bgcolor: 'error.light', 
            color: 'error.contrastText',
            borderRadius: 1,
            fontSize: '0.875rem',
            textAlign: 'center'
          }}
        >
          {error}
        </Typography>
      )}

      <Box sx={{ 
        flex: 1, 
        overflowY: 'auto',
        display: 'flex',
        flexDirection: 'column',
        gap: 1
      }}>
        {documents.length > 0 ? (
          documents.map((doc) => (
            <Tooltip key={doc} title={doc} placement="left">
              <FormControlLabel
                control={
                  <Checkbox
                    checked={selectedDocs.includes(doc)}
                    onChange={() => handleSelectionChange(doc)}
                  />
                }
                label={doc}
                sx={{
                  '& .MuiFormControlLabel-label': {
                    width: '100%',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    whiteSpace: 'nowrap'
                  }
                }}
              />
            </Tooltip>
          ))
        ) : !loading && (
          <Typography 
            color="text.secondary" 
            align="center"
            sx={{ 
              p: 2, 
              bgcolor: 'grey.50', 
              borderRadius: 1,
              fontSize: '0.875rem'
            }}
          >
            No documents uploaded yet. Upload a document to get started.
          </Typography>
        )}
      </Box>
    </Box>
  );
}

export default DocumentManager;
